import React, { useState, useCallback } from 'react';
import { Card, Button, Popover, Avatar } from 'antd';
import { RetweetOutlined, MessageOutlined, HeartOutlined, EllipsisOutlined, HeartTwoTone } from '@ant-design/icons';
import { useSelector } from 'react-redux';
import PostImages from './PostImages'

const PostCard = ({ post }) => {
    const [liked, setLiked] = useState(false);
    const [commentFormOpened, setCommentFormOpened] = useState(false);
    const onToggleLiked = useCallback(() => {
        setLiked((prev) => !prev);

    }, [])
    //로그인을 한 상태일 때 조건문 만들기
    const { user } = useSelector((state) => state.user)
    const id = user && user.id

    return (
        <div style={{ marginBottom: 10 }}>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetweetOutlined key="retweet" />,
                    liked ? <HeartTwoTone twoToneColor="#eb2f96" onClick={onToggleLiked} /> : <HeartOutlined key="heart" onClick={onToggleLiked} />,
                    <MessageOutlined key="comment" />,
                    <Popover key="more" content={(
                        <Button.Group>
                            {/* 내 아이디가 있고 게시글 작성자 아이디가 나랑 같으면 수정 삭제버튼 보이고 다르면 신고버튼 보이게*/}
                            {id && post.User.id === id ? (
                                <>
                                    <Button>수정</Button>
                                    <Button type="danger">삭제</Button>
                                </>)
                                :
                                <Button>신고</Button>}


                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}

            >
                <Card.Meta
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    title={post.User.nickname}
                    description={post.content}
                />
            </Card>
            {/* <CommentForm />
            <Comments /> */}
        </div>

    )
}

export default PostCard
