export const initialState = {
    mainPosts: [{
        id: 1,
        User: {
            id: 1,
            nickname: 'Chloe',
        },
        content: '첫 번째 게시글 #헤시태그 #익스프레스',
        Images: [{

        }, {

        }, {

        }],
        Comments: [
            {
                user: {
                    nickname: 'nero',
                },
                content: '우아아'
            },
            {
                user: {
                    nickname: 'hero',

                },
                content: '신기하다'

            }
        ]
    }],
    //업로드 하는 이미지 경로들
    imagePaths: [],
    //포스트 추가가 완료되면 true 
    postAdded: false,
};

const ADD_POST = 'ADD_POST';

export const addPost = {
    type: ADD_POST
};


export const dummyPost = {
    id: 2,
    content: '더미데이터입니다.',
    User: {
        id: 1,
        nickname: 'Chloe'
    },
    Images: [],
    Comments: [],

};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST:
            return {
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts],
                postAdded: true,
            }

        default:
            return state;
    }
}



export default reducer
