export const initialState = {
    mainPosts: [{
        id: 1,
        User: {
            id: 1,
            nickname: 'Chloe',
        },
        content: '첫 번째 게시글 #헤시태그 #익스프레스',
        Images: [{
            src: 'https://i.pinimg.com/474x/3e/29/14/3e29149409aafb78f61ff6f3ff0c9f98.jpg'

        }, {
            src: 'https://i.pinimg.com/474x/ea/05/f5/ea05f54b3824fbc4ba2345e873190b33.jpg'

        }, {
            src: 'https://i.pinimg.com/474x/ac/f8/5a/acf85a2ae3a939ba695b6af517302155.jpg'

        }],
        Comments: [
            {
                User: {
                    nickname: 'nero',
                },
                content: '우아아'
            },
            {
                User: {
                    nickname: 'hero',

                },
                content: '신기하다'

            }
        ]
    }],
    //업로드 하는 이미지 경로들
    imagePaths: [],
    //포스트 추가가 완료되면 true 
    postAdded: false,
};

const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
const ADD_POST_FAILURE = 'ADD_POST_FAILURE';


export const addPost = (data) => {
    type: ADD_POST_REQUEST,
        data
};


export const dummyPost = {
    id: 2,
    content: '더미데이터입니다.',
    User: {
        id: 1,
        nickname: 'Chloe'
    },
    Images: [{
        src: 'file:///Users/junmishin/Documents/KakaoTalk_Photo_2021-06-03-17-47-52.jpeg'
    }],
    Comments: [],

};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST_SUCCESS:
            return {
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts],
                postAdded: true,
            }

        default:
            return state;
    }
}



export default reducer
