    ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE, REMOVE_POST_REQUEST, REMOVE_POST_SUCCESS, REMOVE_POST_FAILURE, LOAD_POSTS_REQUEST, LOAD_POSTS_SUCCESS, LOAD_POSTS_FAILURE, LIKE_POST_REQUEST, UNLIKE_POST_REQUEST, LIKE_POST_SUCCESS, LIKE_POST_FAILURE, UNLIKE_POST_SUCCESS, UNLIKE_POST_FAILURE, UPLOAD_IMAGES_REQUEST, UPLOAD_IMAGES_SUCCESS, UPLOAD_IMAGES_FAILURE, LOAD_POST_SUCCESS, LOAD_POST_FAILURE, LOAD_POST_REQUEST

} from '../reducers/post'

import { ADD_POST_TO_ME, REMOVE_POST_OF_ME } from '../reducers/user';
import shorId from 'shortid';
import { generateDummyPost } from '../reducers/post'



function uploadImagesAPI(data) {
    return axios.post('/post/images', data)
}

function* uploadImages(action) {
    try {
        const result = yield call(uploadImagesAPI, action.data);

        yield put({
            type: UPLOAD_IMAGES_SUCCESS,
            data: result.data,
        });

    }
    catch (err) {
        yield put({
            type: UPLOAD_IMAGES_FAILURE,
            data: err.response.data,
        })
    }
};



function likePostAPI(data) {
    return axios.patch(`/post/${data}/like`)
}

function* likePost(action) {
    try {
        const result = yield call(likePostAPI, action.data);

        yield put({
            type: LIKE_POST_SUCCESS,
            data: result.data,
        });

    }
    catch (err) {
        yield put({
            type: LIKE_POST_FAILURE,
            data: err.response.data,
        })
    }
};



function unlikePostAPI(data) {
    return axios.delete(`/post/${data}/like`)
}

function* unlikePost(action) {
    try {
        const result = yield call(unlikePostAPI, action.data);

        yield put({
            type: UNLIKE_POST_SUCCESS,
            data: result.data,
        });

    }
    catch (err) {
        yield put({
            type: UNLIKE_POST_FAILURE,
            data: err.response.data,
        })
    }
};



function addPostAPI(data) {
    return axios.post('/post', data)
}

function* addPost(action) {
    try {
        const result = yield call(addPostAPI, action.data);

        yield put({
            type: ADD_POST_SUCCESS,
            data: result.data,
        });
        yield put({
            type: ADD_POST_TO_ME,
            data: result.data.id,
        })
    }
    catch (err) {
        yield put({
            type: ADD_POST_FAILURE,
            data: err.response.data,
        })
    }
};



function loadPostAPI(data) {
    return axios.get(`/post/${data}`);
}

function* loadPost(action) {
    try {
        const result = yield call(loadPostAPI, action.data);
        yield put({
            type: LOAD_POST_SUCCESS,
            data: result.data,
        });
    } catch (err) {
        console.error(err);
        yield put({
            type: LOAD_POST_FAILURE,
            data: err.response.data,
        });
    }
}


function loadPostsAPI(lastId) {
