export const initialState = {
    isLoggingIn: false, //로그인 시도중 
    isLoggedIn: false,
    isLoggingOut: false, //로그아웃 시도중
    user: null,
    signUpData: {},
    loginData: {},

};


//액션 크리에이터
export const loginRequestAction = (data) => {
    return {
        type: 'LOG_IN_REQUEST',
        data,
    }
}

export const logoutRequestAction = (data) => {
    return {
        type: 'LOG_OUT_REQUEST',
        data,
    }
};







const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOG_IN_REQUEST':
            return {

                ...state,
                isLoggingIn: true,

            };

        case 'LOG_IN_SUCCESS':
            return {

                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                user: { ...action.data, nickname: 'Chloe' }

            };
        case 'LOG_IN_FAILURE':
            return {

                ...state,
                isLoggingIn: false,

                isLoggedIn: false,

            };
        case 'LOG_OUT_REQUEST':
            return {

                ...state,
                isLoggingOut: true,
                isLoggedIn: false,
                user: null,

            };
        case 'LOG_OUT_SUCCESS':
            return {

                ...state,
                isLoggingOut: false,
                isLoggedIn: false,
                user: null,

            };
        case 'LOG_OUT_FAILURE':
            return {

                ...state,
                isLoggingOut: false,

            };
        default:
            return state;
    }
};

export default reducer
