            },
            {
                user: {
                    nickname: 'hero',

                },
                content: '신기하다'

            }
        ]
    }],
    //업로드 하는 이미지 경로들
    imagePaths: [],
    //포스트 추가가 완료되면 true 
    postAdded: false,
};

const ADD_POST = 'ADD_POST';

export const addPost = {
    type: ADD_POST
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        default:
            return state;
    }
}



export default reducer
