    user: {


    },
    post: {

    }
};




const rootReducer = combineReducers({
    index: (state = {}, action) => {
        switch (action.type) {
            case HYDRATE:
                return { ...state, ...action.payload };

            default:
                return state;

        }
    },
    user, post,

});

